body {
  margin: 0;
  display: grid;
  grid-template-columns: 1fr;
  grid-auto-rows: 40vh 50vh 10vh;
  color: white;
  background-color: #cfcfcf;
  font-family: "Raleway", sans-serif;
}

header {
  text-align: center;
  grid-row: 1;
}
header h1 {
  font-size: 3em;
}

main {
  grid-row: 2;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
}

.squares-container {
  width: 260px;
  margin-top: 20px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
}

.game-square {
  margin: 1vh;
  height: 14vh;
  width: 14vh;
  border: 1px solid white;
  border-radius: 50%;
}

#green {
  background-color: green;
  opacity: 0.3;
}

#red {
  background-color: red;
  opacity: 0.3;
}

#yellow {
  background-color: yellow;
  opacity: 0.3;
}

#blue {
  background-color: blue;
  opacity: 0.3;
}

.highlighted {
  border: 5px solid gold;
}

.responses-container {
  display: flex;
  justify-content: space-around;
  align-items: center;
}
.responses-container p {
  margin: 2vh;
}

footer {
  grid-row: 3;
  display: flex;
  justify-content: flex-end;
  align-items: center;
  margin-right: 3vw;
}
