body {
  margin: 0;
  display: grid;
  grid-template-columns: 1fr;
  grid-auto-rows: 30vh 45vh 17vh 8vh;
  color: white;
  background-color: #cfcfcf;
  font-family: "Roboto", sans-serif;
}

header {
  text-align: center;
  grid-row: 1;
}
header h1 {
  font-size: 3em;
}
header h4 {
  margin: -10px 0 40px 0;
}

main {
  grid-row: 2;
  display: flex;
  justify-content: center;
  align-items: center;
}
main .buttons-container {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: flex-end;
}
main .squares-container {
  width: 280px;
  margin: 0 60px 0 60px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
}

button {
  font-size: 1.1em;
  font-family: "Righteous", cursive;
  margin: 3vh 0;
}

.game-square {
  margin: 0.2em;
  height: 14vh;
  width: 14vh;
  border: 3px solid white;
  border-radius: 50%;
}

#green {
  background-color: green;
  opacity: 0.3;
}

#red {
  background-color: red;
  opacity: 0.3;
}

#yellow {
  background-color: yellow;
  opacity: 0.3;
}

#blue {
  background-color: blue;
  opacity: 0.3;
}

.highlighted {
  border: 5px solid gold;
}

.responses-container {
  display: flex;
  justify-content: space-around;
  align-items: center;
}
.responses-container p {
  margin: 2vh;
}

section {
  grid-row: 3;
}
section .responses-container {
  display: flex;
  justify-content: center;
  align-items: center;
}
section .responses-container p {
  margin: 2vh;
}

footer {
  grid-row: 4;
  display: flex;
  justify-content: flex-end;
  align-items: center;
  margin-right: 3vw;
}
