generated.css:
body {
  margin: 0;
  display: grid;
  grid-template-columns: 1fr;
  grid-auto-rows: 18vh 72vh 10vh;
  color: white;
  background-image: url("fdbe8169c97bcc29ae52e650a3a6d110.png");
  font-family: "Righteous", cursive;
}

header {
  text-align: center;
  grid-row: 1;
}
header h1 {
  font-size: 3em;
}
header h4 {
  margin: -10px 0 40px 0;
}

main {
  grid-row: 2;
  display: flex;
  justify-content: center;
  align-items: center;
}
main .buttons-container {
  width: 280px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: flex-end;
}
main .squares-container {
  width: 250px;
  height: 250px;
  margin: 8vh;
  padding: 5vh;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
  border: 10px solid #c5c5c5;
  border-radius: 50%;
  background-color: #222222;
}
main .squares-container .game-square {
  margin: -1em 0.5em;
  height: 14vh;
  width: 14vh;
  border: 3px solid white;
  border-radius: 50%;
  opacity: 0.4;
}
main .levels-container {
  width: 280px;
}

button {
  font-size: 1.1em;
  margin: 3vh 0;
  border: 2px solid black;
  font-family: "Righteous", cursive;
}

#green {
  background-color: green;
}

#red {
  background-color: red;
}

#yellow {
  background-color: yellow;
}

#blue {
  background-color: blue;
}

.highlighted {
  opacity: 1 !important;
}

footer {
  grid-row: 3;
  display: flex;
  justify-content: flex-end;
  align-items: center;
  margin-right: 3vw;
  font-family: "Raleway", sans-serif;
}
